
public class Rotating {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1="gamya";
		String s2="amyag";
		String t=s1.concat(s1);
		if(t.contains(s2))
		{
			System.out.println("Rotating String");
		}
		else
		{
			System.out.println("Not Rotating String");
		}
	}

}
